INSERT INTO mechanisms (tactic_id, step_description) VALUES ((SELECT id FROM tactics WHERE name = 'Misinformation Seeding'), 'Tactical Introduction: Orchestrators inject narrative fragments into trusted channels—water-cooler talk, community boards, group chats—so it seems like grassroots concern.');
INSERT INTO mechanisms (tactic_id, step_description) VALUES ((SELECT id FROM tactics WHERE name = 'Misinformation Seeding'), 'Ambiguity & Plausible Deniability: Vague labels ("something''s off") avoid specifics, making direct denial difficult and encouraging rumor growth.');
INSERT INTO mechanisms (tactic_id, step_description) VALUES ((SELECT id FROM tactics WHERE name = 'Misinformation Seeding'), 'Repetition & Amplification: As multiple civilians repeat variations, the story morphs into an accepted fact. Look for clusters of similar statements across unrelated sources.');
